y-3.3.1.js"></script>    <script type="text/javascript" src="https://cdn.datatables.net/1.10.19/js/jquery.dataTables.min.js"></script>    <script type="text/javascript" src="https://cdn.datatables.net/1.10.19/js/dataTables.bootstrap.min.js"></script>    <script src="https://codeseven.github.io/toastr/build/toastr.min.js"></script>    <script>        $(function () {            $('body').on('hidden.bs.modal', '.modal', function () {                $(this).removeData('bs.modal');            });        });        $(function() {            var status = null;            var select_status = $('#status').val();            $('#status').on('change',function(){                status = $(this).val();                table.draw();            });            var table = $('#table').DataTable( {                // dom:'<"m-t-10 pull-left"l><"m-t-10 pull-right"f>rti<"m-t-10 pull-left"B><"m-t-10 pull-right"p>',                dom:'<"m-t-10 pull-right"f>rti<"m-t-10 pull-right"p><"m-t-10 pull-bottom"l>',                processing: true,                serverSide: true,                "lengthMenu": [[10, 20, 40, 100], [10, 20, 40, 100]],                ajax: {                    url: '{!! route('agents.data') !!}',                    data: function (d) {                        d.status = status;                        d.select_status = select_status;                    }                },                order: [[0, 'asc']],                columns: [                    { data: 'checkbox', name: 'checkbox', orderable: false, searchable: false  },                    { data: 'id', name: 'id' },                    { data: 'name', name: 'name' },                    { data: 'wallet_amt', name: 'wallet_amt' },                    { data: 'mobile', name: 'mobile' },                    { data: 'email', name: 'email' },                    { data: 'address1', name: 'address1' },                    { data: 'city', name: 'city' },                    { data: 'status', name: 'status' },                    { data: 'actions', name: 'actions', orderable: false, searchable: false }                ],            });        } );        //Validation on Checkbox Form        function updateStatus() {            var update_status = $('#action').val();            if(update_status != -1){                if($('.checkBoxClass').is(':checked')){                    document.getElementById("UpdateStatusForm").submit();                }else{                    toastr.error('Please Select Atleast One Checkbox');                }            }else{                toastr.error('Please Select Atleast One Status');            }        }        //Script For All Checkbox Check        $("#checkAll").click(function () {            $('input:checkbox').not(this).prop('checked', this.checked);        });    </script>@endsection